mapper.select:
SELECT 

FROM exception_report a

WHERE a.id = #{id}

SELECT 

FROM exception_report a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.address like concat('%',#{address},'%')


		AND b.name like concat('%',#{createByName},'%')


		AND a.create_date >= #{beginCreateDate}


		AND a.create_date <= #{endCreateDate}


		AND a.check_status = #{checkStatus}


		AND a.check_date >= #{beginCheckDate}


		AND a.check_date <= #{endCheckDate}





		ORDER BY ${page.orderBy}

SELECT 

FROM exception_report a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE exception_report SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
