mapper.select:
SELECT 

FROM cover_work_inspection a

WHERE a.id = #{id}

SELECT 

FROM cover_work_inspection a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.cover_work_num = #{coverWorkNum}


		AND a.area LIKE 
			'%'||#{area}||'%'
			'%'+#{area}+'%'
			concat('%',#{area},'%')


		AND a.signin_person_name = #{signInPersonName}


		AND a.signin_time BETWEEN #{beginSignInTime} AND #{endSignInTime}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM cover_work_inspection a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE cover_work_inspection SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
