mapper.select:
SELECT

FROM cover_statis a

WHERE a.id = #{id}

SELECT

FROM cover_statis a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.create_date BETWEEN #{beginCreateDate} AND #{endCreateDate}


		AND a.district LIKE
		'%'||#{district}||'%'
		'%'+#{district}+'%'
		concat('%',#{district},'%')


		AND a.cover_type = #{coverType}


		AND a.owner_depart = #{ownerDepart}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT

FROM cover_statis a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE cover_statis SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT
a.district AS district,
sum(a.cover_num) AS coverNum,
sum(a.install_equ) AS installEqu,
sum(a.online_num) AS onlineNum,
sum(a.offline_num) AS offlineNum,
sum(a.cover_alarm_num) AS coverAlarmNum,
sum(a.alarm_total_num) AS alarmTotalNum,
sum(a.add_work_num) AS addWorkNum,
sum(a.complete_work_num) AS completeWorkNum,
sum(a.pro_work_num) AS proWorkNum,
a.statis_time AS statisTime
FROM cover_statis a
WHERE
1=1

    AND a.district = #{district}


    AND a.cover_type = #{coverType}


    AND a.owner_depart = #{ownerDepart}


    AND a.create_date >= #{beginCreateDate}


    AND a.create_date <=#{endCreateDate}



        AND a.create_date >= #{beginCreateDate}


        AND a.create_date <=#{endCreateDate}

GROUP BY a.district
