mapper.select:
SELECT 

FROM office_owner_statis a

WHERE a.id = #{id}

SELECT 

FROM office_owner_statis a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.office_id = #{officeId}


		AND a.office_name = #{officeName}


		AND a.owner_depart = #{ownerDepart}


		AND a.statis_time = #{statisTime}


		AND a.flag = #{flag}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM office_owner_statis a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE office_owner_statis SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT

FROM office_owner_statis a


	a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.office_id is not null
	${dataScope}

		AND a.statis_time = #{statisTime}



group by a.office_name


		ORDER BY ${page.orderBy}


		ORDER BY a.completion_rate DESC

SELECT

FROM office_owner_statis a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.statis_time = #{statisTime}



group by a.owner_depart


		ORDER BY ${page.orderBy}


		ORDER BY a.completion_rate DESC
