mapper.select:
SELECT 

FROM flow_state a

WHERE a.id = #{id}

SELECT 

FROM flow_state a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.state_name = #{stateName}


		AND a.state_code = #{stateCode}


		AND a.life_cycle = #{lifeCycle}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM flow_state a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE flow_state SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
