mapper.select:
SELECT 

FROM flow_opt_result a

WHERE a.id = #{id}

SELECT 

FROM flow_opt_result a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.opt_id = #{optId.id}


		AND a.result = #{result}


		AND a.project_name  = #{projectName}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM flow_opt_result a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE flow_opt_result SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
