SELECT 

FROM project_info a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.project_no = #{projectNo}


		AND a.project_name = #{projectName}


		AND a.office_id = #{office.id}


		AND a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM project_info a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


