mapper.select:
SELECT 

FROM sys_dict_type a

WHERE a.id = #{id}

SELECT
FROM sys_dict_type a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.type LIKE '%'||#{type}||'%' '%'+#{type}+'%' concat('%',#{type},'%')
AND a.description LIKE '%'||#{description}||'%' '%'+#{description}+'%' concat('%',#{description},'%')
AND a.project_id = #{projectId}
AND a.type = #{type}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT
FROM sys_dict_type a
a.del_flag = 0 and description in #{item}

SELECT 

FROM sys_dict_type a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.project_id = #{projectId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sys_dict_type SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
