mapper.select:
SELECT 

FROM msg_push_config a

WHERE a.id = #{id}

SELECT 

FROM msg_push_config a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')


		AND a.notice_office_id = #{noticeOfficeId}


		AND a.notice_type = #{noticeType}


		AND a.state = #{state}


		AND a.notice_person = #{noticePerson.id}


		AND a.create_date BETWEEN #{beginCreateDate} AND #{endCreateDate}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM msg_push_config a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE msg_push_config SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
